# INITIAL ADMIN DB
# This is the schema of the testimonial module database as of 09/01/2015
DROP TABLE IF EXISTS `nails_testimonial`;
CREATE TABLE `nails_testimonial` (`id` int(11) unsigned NOT NULL AUTO_INCREMENT, `quote` text, `quote_by` varchar(150) DEFAULT NULL, `order` tinyint(1) unsigned NOT NULL, `created` datetime NOT NULL, `created_by` int(11) unsigned DEFAULT NULL, `modified` datetime NOT NULL, `modified_by` int(11) unsigned DEFAULT NULL, PRIMARY KEY (`id`), KEY `created_by` (`created_by`), KEY `modified_by` (`modified_by`), CONSTRAINT `nails_testimonial_ibfk_1` FOREIGN KEY (`created_by`) REFERENCES `nails_user` (`id`) ON DELETE SET NULL, CONSTRAINT `nails_testimonial_ibfk_2` FOREIGN KEY (`modified_by`) REFERENCES `nails_user` (`id`) ON DELETE SET NULL) ENGINE=InnoDB DEFAULT CHARSET=utf8;